import React, { useEffect, useState } from "react";
import axios from 'axios';
import Menu from './Menu'
import '../styles/custom_contact.css'

export default function Contact(){

    const [contactDetails, setContactDetails] = useState([]);

    useEffect(() => {

        const getContactDetails = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:9000/api/getcontactdetails');
                setContactDetails(response.data.contactdetails);
                console.log(response.data);
            } catch (e) {
                throw console.error('problem on api', e);
            }
        }

        getContactDetails();
    }, []);

    return (
        <div>
            <Menu />
            <div className="ui container">
                <h1 className="contact_h1">Contact Us</h1>
                <div className="ui container">
                    <div className="ui segment">
                        <div className="ui list">
                        <div className="item">
                            <i className="phone icon"></i>
                            <div className="content">
                                {contactDetails.map(contact => (
                                    <a href="/contactus">Contact Number: {contact.contact_no }</a>
                                ))}
                            </div>
                        </div>
                        <div className="item">
                            <i className="phone icon"></i>
                            <div className="content">
                                {contactDetails.map(contact => (
                                    <a href="/contactus">Contact Number: {contact.tel_no }</a>
                                ))}
                            </div>
                        </div>
                        <div className="item">
                            <i className="mail icon"></i>
                            <div className="content">
                            <a href="mailto:info@example.com">Email Address: info@example.com</a>
                            </div>
                        </div>
                        <div className="item">
                            <i className="building icon"></i>
                            <div className="content">
                            Company Address: 123 Street, City, Country
                            </div>
                        </div>
                        <div className="item">
                            <i className="facebook icon"></i>
                            <div className="content">
                            <a href="https://www.facebook.com/example">Facebook Page: Example</a>
                            </div>
                        </div>
                        <div className="item">
                            <i className="instagram icon"></i>
                            <div className="content">
                            <a href="https://www.instagram.com/example">Instagram: @example</a>
                            </div>
                        </div>
                        <div className="item">
                            <i className="twitter icon"></i>
                            <div className="content">
                            <a href="https://twitter.com/example">Twitter: @example</a>
                            </div>
                        </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
